import React, {useState} from 'react';

function Todo(){

    const [todos, setTodos] = useState([ ]);
    const [todo, settodo] = useState('');

    const handleTodos = e => {
        settodo(e.target.value);
    }

    const addTodos = () => {
        const newTodos = [...todos, { todo }];
        setTodos(newTodos);
        settodo('');
      };

    const completeTodos = index => {
        const newTodos = [...todos];
        newTodos[index].isCompleted = true;
        setTodos(newTodos);
      };

    const removeTodo = index => {
        const newTodos = [...todos];
        newTodos.splice(index, 1);
        setTodos(newTodos);
      };

      const editTodo = index => {
        const newTodos = [...todos];
        const a=newTodos[index];
        settodo(a);
      }

    return(
        <div>
            <h1>Assignment No.3 in React (Todo List)</h1>
            <input type="todo" onChange={handleTodos} value={todo}/>
            <button onClick={() => addTodos()}> Add </button>
            <ul>
                {todos.map((item,index) => (
                    <li  style={{ textDecoration: item.isCompleted ? "line-through" : "" }}>
                        {item.todo} 
                    <button onClick={() => {completeTodos(index)}}> Complete </button> 
                    <button onClick={() => {removeTodo(index)}}> Delete </button> 
                    <button onClick={() => {editTodo(index)}}> Edit </button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default Todo;
